PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `SequelizeMeta` (`name` VARCHAR(255) NOT NULL UNIQUE PRIMARY KEY);
CREATE TABLE `Users` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `firstName` VARCHAR(255) NOT NULL, `lastName` VARCHAR(255) NOT NULL, `userName` VARCHAR(255) NOT NULL UNIQUE, `hashedPassword` VARCHAR(255) NOT NULL, `email` VARCHAR(255) NOT NULL UNIQUE, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Spots` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `ownerId` INTEGER NOT NULL REFERENCES `Users` (`id`) ON DELETE CASCADE, `address` VARCHAR(255) NOT NULL, `city` VARCHAR(255) NOT NULL, `state` VARCHAR(255) NOT NULL, `country` VARCHAR(255) NOT NULL, `lat` FLOAT NOT NULL, `lng` FLOAT NOT NULL, `name` VARCHAR(255) NOT NULL, `description` VARCHAR(255), `price` FLOAT NOT NULL, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `Bookings` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `userId` INTEGER NOT NULL REFERENCES `Users` (`id`) ON DELETE CASCADE, `spotId` INTEGER NOT NULL REFERENCES `Spots` (`id`) ON DELETE CASCADE, `startDate` DATETIME NOT NULL, `endDate` DATETIME NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Reviews` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `spotId` INTEGER NOT NULL REFERENCES `Spots` (`id`) ON DELETE CASCADE, `userId` INTEGER NOT NULL REFERENCES `Users` (`id`) ON DELETE CASCADE, `review` VARCHAR(255) NOT NULL, `stars` FLOAT NOT NULL, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `ReviewImages` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `reviewId` INTEGER NOT NULL REFERENCES `Reviews` (`id`) ON DELETE CASCADE, `url` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `SpotImages` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `spotId` INTEGER NOT NULL REFERENCES `Spots` (`id`) ON DELETE CASCADE, `url` VARCHAR(255) NOT NULL, `preview` TINYINT(1) NOT NULL DEFAULT 0, `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE `SequelizeData` (`name` VARCHAR(255) NOT NULL UNIQUE PRIMARY KEY);
DELETE FROM sqlite_sequence;
COMMIT;
